    \draw[->] (0,0,0) -- (3,0,0) node[right]{$x$};
    \draw[->] (0,0,0) -- (0,3,0) node[above]{$y$};
    \draw[->] (0,0,0) -- (0,0,3) node[below left]{$z$};
    \draw[thick] (0,0,0) -- (2,0,0) -- (2,2,0) -- (0,2,0) -- cycle;
    \draw[thick] (0,0,0) -- (0,0,2);
