    \draw[thick] (0,2,0) -- (0,2,2) -- (2,2,2);
    \draw[thick] (0,0,2) -- (0,2,2);
    \addplot3[quiver,-stealth,samples=10] {x*y*z};
\end{tikzpicture}
\begin{animateinline}[controls,loop]{10}
\multiframe{20}{rX=0+0.1}{ % rX is a variable that controls the animation
\tdplotsetmaincoords{60}{110}
\begin{tikzpicture}[scale=2,tdplot_main_coords]
    \draw[->] (0,0,0) -- (3,0,0) node[right]{$x$};
    \draw[->] (0,0,0) -- (0,3,0) node[above]{$y$};
