class Instrument {
  constructor(name, type) {
    this.name = name;
    this.type = type;
  }
  play() {
    console.log(`Playing ${this.name}...`);
  }
}
class StringInstrument extends Instrument {
  constructor(name, type, strings, pitch, tone) {
    super(name, type);
    this.strings = strings;
    this.pitch = pitch;
    this.tone = tone;
  }
  pluckString(stringNum) {
    console.log(`Plucking string ${stringNum}...`);
  }
