  }
  changeTone() {
    console.log(`Changing the tone of ${this.name}...`);
  }
}
class WindInstrument extends Instrument {
  constructor(name, type, reed, volume, tone) {
    super(name, type);
    this.reed = reed;
    this.volume = volume;
    this.tone = tone;
  }
  blow() {
    console.log(`Blowing ${this.reed}...`);
  }
  controlVolume() {
    console.log(`Controlling the volume of ${this.name}...`);
  }
  controlTone() {
    console.log(`Controlling the tone of ${this.name}...`);
  }
}
class PercussionInstrument extends Instrument {
  constructor(name, type) {
    super(name, type);
  }
  strike() {
    console.log(`Striking ${this.name}...`);
  }
  drumroll() {
    console.log(`Playing a drumroll on ${this.name}...`);
  }
}
const guitar = new StringInstrument('guitar', 'string', 6, 0, 'default');
guitar.play();
guitar.pluckString(3);
guitar.changePitch();
guitar.changeTone();
const trumpet = new WindInstrument('trumpet', 'wind', 'brass', 0, 0);
trumpet.play();
trumpet.blow();
trumpet.controlVolume();
trumpet.controlTone();
const drums = new PercussionInstrument('drums', 'percussion');
drums.play();
drums.strike();
drums.drumroll();

