import Foundation
// Load TV shows from XML data
guard let xmlData = """
<?xml version="1.0" encoding="UTF-8"?>
<tv_shows>
  <show decade="1950s">I Love Lucy</show>
  <show decade="1950s">The Honeymooners</show>
