import Foundation
let frequency: Double = 440 // in Hertz
let amplitude: Double = 1 // maximum amplitude of the wave
let duration: Double = 5 // in seconds
let sampleRate: Double = 44100 // in Hertz
let phaseShift: Double = 0 // in radians
let sampleCount = Int(duration * sampleRate)
