if err != nil {
    log.Fatal(err)
}
// Run inference on multi-modal inputs
start := time.Now()
result, err := model.Session.Run(
    map[tf.Output]*tf.Tensor{
        model.Graph.Operation("serving_default_image_input").Output(0): imgTensor,
        model.Graph.Operation("serving_default_audio_input").Output(0): audioTensor,
    },
    []tf.Output{
